import React, { useState } from 'react';
import EveningData from './PagesData/EveningData';
import MorningData from './PagesData/MorningData';
import Logo from '../assets/logo.png';
import { ToastContainer, toast } from 'react-toastify';
import './BookingHours.css';
import { useNavigate } from 'react-router-dom';
import Spinner from '../Components/Spinner';

const BookingHours = () => {
  const url = 'https://dental-service.onrender.com/dental-clinic/slot';
  const navigate = useNavigate();
  const [loader, setLoader] = useState('none');
  const [activeUser, setActiveUser] = useState({
    date: '',
    name: '',
    email: '',
    phone: '',
    time: '',
  });
  const [btn, setBtn] = useState(0);
  const [aces, setACES] = useState(-1);
  const [ace, setACE] = useState(-1);

  const toastOptions = {
    position: 'top-right',
    autoClose: 8000,
    pauseOnHover: true,
    draggable: true,
    theme: 'dark',
  };

  let name, value;
  const handleInputs = (e) => {
    name = e.target.name;
    value = e.target.value;
    setActiveUser({ ...activeUser, [name]: value });
  };

  function checkDate(selectedDate) {
    var now = new Date();
    var formattedDate = now.toISOString().split('T')[0];
    console.log(formattedDate);
    if (selectedDate < formattedDate) {
      alert('Date must be in the future');
      return false;
    }

    return true;
  }

  const handleValidation = () => {
    const { date, name, email, phone, time } = activeUser;
    if (date === '') {
      toast.error('Choose the Date', toastOptions);
      return false;
    } else if (name === '') {
      toast.error('Enter your name', toastOptions);
      return false;
    } else if (email === '') {
      toast.error('Enter your Email', toastOptions);
      return false;
    } else if (phone === '') {
      toast.error('Enter Your Currect Phone No', toastOptions);
      return false;
    } else if (time === '') {
      toast.error('Choose your slot timing', toastOptions);
      return false;
    } else if (
      date === '' ||
      name === '' ||
      email === '' ||
      phone === '' ||
      time === ''
    ) {
      toast.error('Plz Enter Your all details', toastOptions);
      return false;
    }
    return true;
  };

  // const handleSubmit = async (event) => {
  //   event.preventDefault();

  //   const { date, name, email, phone, time } = activeUser;

  //   const requestOptions = {
  //     date,
  //     name,
  //     email,
  //     phone,
  //     time,
  //   };
  //   console.log(requestOptions);

  //   if (handleValidation()) {
  //     setBtn(1);
  //     setLoader('flex');
  //     const res = await fetch(url, {
  //       method: 'POST',
  //       headers: {
  //         'Content-Type': 'application/json',
  //       },
  //       body: JSON.stringify(requestOptions),
  //     });

  //     const data = await res.json();

  //     if (data) {
  //       setBtn(0);
  //       setLoader('none');
  //     }
  //     if (data.status === 201) {
  //       console.log("Your data submitted to me it's server");
  //       toast.success(data.message, toastOptions);

  //       setTimeout(() => {
  //         navigate('/');
  //       }, 4000);
  //     } else if (data.status === 401) {
  //       toast.error(data.message, toastOptions);
  //     }
  //   }
  // };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const { date, name, email, phone, time } = activeUser;
    const requestOptions = {
      date,
      name,
      email,
      phone,
      time,
    };

    console.log(requestOptions);

    if (handleValidation()) {
      setBtn(1);
      setLoader('flex');

      try {
        const res = await fetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(requestOptions),
        });

        if (res.status === 201) {
          console.log('Tus datos se han enviado correctamente al servidor');
          toast.success('Cita realizada con éxito. ', toastOptions);

          const form = event.target;
          form.reset();

          setTimeout(() => {
            navigate('/');
          }, 4000);
        } else if (res.status === 401) {
          const data = await res.json();
          toast.error(data.message, toastOptions);
        } else {
          console.error('Lo siento, estoy teniendo problemas para responder a las solicitudes en este momento. Inténtalo de nuevo dentro de un rato.');
          toast.error(
            'Se produjo un error al procesar tu solicitud.',
            toastOptions
          );
        }
      } catch (err) {
        console.error(err);
        toast.error(
          'Se produjo un error al procesar tu solicitud.',
          toastOptions
        );
      } finally {
        setBtn(0);
        setLoader('none');
      }
    }
  };

  return (
    <>
      <div className="booking_section_container">
        <div className="bsc_lower">
          <form method="POST" onSubmit={handleSubmit}>
            <div className="bsc_lower_container">
              <div className="bsc_header">
                <div className="appointment_hours_form">
                  <div className="form_for_booking">
                    <div className="brand">
                      <img src={Logo} alt="logo" />
                      <h1>Clínica Odontológica EMERALDENT.</h1>
                    </div>
                    <div className="in__container">
                      <label>Elige Fecha</label>
                      <input
                        type="date"
                        placeholder="Select Date"
                        name="date"
                        style={{ color: 'White' }}
                        value={activeUser.date}
                        onChange={(event) => {
                          const selectedDate = event.target.value;
                          console.log(selectedDate);
                          if (checkDate(selectedDate)) {
                            handleInputs(event);
                          }
                        }}
                        required
                      />
                    </div>
                    <div className="in__container">
                      <label>Tu Nombre</label>
                      <input
                        type="text"
                        placeholder="Enter your name"
                        name="name"
                        min="3"
                        value={activeUser.name}
                        onChange={handleInputs}
                        required
                      />
                    </div>
                    <div className="in__container">
                      <label>Correo</label>
                      <input
                        type="email"
                        placeholder="Enter your Email"
                        name="email"
                        min="3"
                        value={activeUser.email}
                        onChange={handleInputs}
                        required
                      />
                    </div>
                    <div className="in__container">
                      <label>Tu telefono </label>
                      <input
                        type="number"
                        placeholder="Phone No"
                        name="phone"
                        value={activeUser.phone}
                        onChange={handleInputs}
                        onKeyPress={(event) => {
                          if (event.target.value.length >= 10) {
                            event.preventDefault();
                          }
                        }}
                        required
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="me_slot_selection">
                <div className="bsc_lower_morning_container">
                  <span>Turnos de mañana y tarde.</span>
                  <div className="morning_info_container" id="container45">
                    {MorningData.map((data, index) => {
                      return (
                        <div
                          className="md_data"
                          style={{
                            backgroundColor:
                              aces === index ? data.color[0] : 'white',
                            color: aces === index ? data.color[1] : 'black',
                          }}
                          onClick={() => {
                            setACES(index);
                            setActiveUser({
                              ...activeUser,
                              time: data.m_slot_time,
                            });
                          }}
                          key={index}
                        >
                          {data.m_slot_time}
                        </div>
                      );
                    })}
                  </div>
                </div>
                <hr />
                <div className="bsc_lower_evening_container">
                  <div className="evening_info_container">
                    {EveningData.map((data, index) => {
                      return (
                        <div
                          className="ed_data"
                          key={index}
                          style={{
                            backgroundColor:
                              ace === index ? data.color[0] : 'white',
                            color: ace === index ? data.color[1] : 'black',
                          }}
                          onClick={() => {
                            setACE(index);
                            setActiveUser({
                              ...activeUser,
                              time: data.e_slot_time,
                            });
                          }}
                        >
                          {data.e_slot_time}
                        </div>
                      );
                    })}
                    <div className="submit_slot_btn">
                      <button className="booking_c_btn" id="bcb" type="submit">
                        <span style={btn === 1 ? { display: 'none' } : {}}>
                          Submit
                        </span>
                        <Spinner id="sb_loader" style={loader} />
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </form>
          <ToastContainer />
        </div>
      </div>
    </>
  );
};

export default BookingHours;
